"use strict";

var PI = 3.14159; //пишутся БОЛЬШИМИ буквами, объявляется только один раз, значение присваивается сразу и не изменяются в течении программы, const не поднимается
console.log(PI);

var MATH = {
   PI: 3.14159
};

MATH.PI = 3.15; // но СВОЙСТВО! объекта постоянной менять можно

console.log(MATH.PI);